void WinScreen(void)
{
  oam_clear();
  
  ppu_off();
  
  vram_adr(NTADR_A(0, 1));
  vram_put(1);
  
  vram_adr(NTADR_A(1, 1));
  
  if(GameState&P2_CAPD) vram_write(Blue, sizeof(Blue));
  else vram_write(Red, sizeof(Red));
  
  ppu_on_all();
}
